import React, { useEffect } from 'react'
import { Route, Switch } from 'react-router-dom';
import Home from './student/home';
import MyCourses from './student/my_courses';
import Wishlists from './student/wishlists';
import Transactions from './student/transactions';
import { useLocation } from 'react-router';
import axios from 'axios';
import { Baseurl } from '../baseurl';
import { useDispatch } from 'react-redux';
import { isLogged } from '../redux/actions';
import { useState } from 'react';
import BasicDetails from './tutor/basic_details';
import Sections from './tutor/sections';
import CreateAcourse from './tutor/create_a_course';
import CreatedCourses from './tutor/created_courses';
import Earnings from './tutor/earnings';
import SectionFiles from './tutor/files';
import ManageCourse from './tutor/manage_course';
import Profile from './profile';
import Navbar from '../landing_page/navbar';
import Footer from '../landing_page/footer';
import Withdraw from './withdraw';


const Dashboard = () => {
    const [profile, setProfile] = useState({});
    const dispatch = useDispatch();
    useEffect(() => {
        let token = sessionStorage.Token
        if (token) {
            axios.post(`${Baseurl}auth/me`, { token: token }).then((res) => {
                if (res.data) {
                    let data = res.data
                    dispatch(isLogged(true))
                    setProfile(data)
                }
            }).catch((error) => {
                if (error.response.status === 401) {
                    dispatch(isLogged(false))
                } else if (error.response.status === 500) {
                    dispatch(isLogged(false))
                }
            })
        } else {
            dispatch(isLogged(false))
        }
    }, [])
    let path = useLocation()
    return (
        <>
            <Navbar dashboard={path.pathname}/>
            <Switch>
                <Route exact path='/dashboard'>
                    <Home firstname={profile.firstname} />
                </Route>

                <Route exact path='/dashboard/my-courses'>
                    <MyCourses />
                </Route>

                <Route exact path='/dashboard/wishlists'>
                    <Wishlists />
                </Route>

                <Route exact path='/dashboard/transactions'>
                    <Transactions />
                </Route>

                <Route exact path='/dashboard/create-a-course'>
                    <CreateAcourse />
                </Route>

                <Route exact path='/dashboard/create-a-course/basic-details'>
                    <BasicDetails />
                </Route>

                <Route exact path='/dashboard/create-a-course/sections'>
                    <Sections />
                </Route>

                <Route exact path='/dashboard/create-a-course/add-files'>
                    <SectionFiles />
                </Route>

                <Route exact path='/dashboard/created-courses/manage/:code'>
                    <ManageCourse />
                </Route>

                <Route exact path='/dashboard/created-courses'>
                    <CreatedCourses />
                </Route>

                <Route exact path='/dashboard/earnings'>
                    <Earnings />
                </Route>

                <Route exact path='/dashboard/profile'>
                    <Profile profile={profile}/>
                </Route>

                <Route exact path='/dashboard/earnings-withdraw'>
                    <Withdraw profile={profile}/>
                </Route>

                <Route exact path='/dashboard/settings'>
                    <Withdraw profile={profile}/>
                </Route>

            </Switch>
      
            <Footer/>
        </>
    )
}

export default Dashboard
