import React, { useState, useEffect } from 'react'
import { Link as Link2, useHistory } from 'react-router-dom';
import { Link } from 'react-scroll';
import Navbar from './navbar';
import Footer from './footer'
import { Baseurl } from '../baseurl';
import axios from 'axios';
import Trending_courses from './trending_courses';
import Latest_courses from './latest_courses';
import Back_to_top from './back_to_top';
import { Formik, Form, Field, ErrorMessage } from 'formik';




const Register = () => {


    return (
        <>
            <Navbar />
            <section className="about container-fluid mt-3" id="about">
                <div className="row">
                    <div className="col-lg-5  d-lg-block order-2 order-lg-1" data-aos="fade-in" data-aos-delay="100">
                             <img src="assets/img/student3.jpg" className="img-fluid login-img" alt="" style={{ borderRadius: '10px' }} />
                   
                    </div>

                    <div className="col-lg-6 order-1 order-lg-2 mb-4" data-aos="fade-in" data-aos-delay="100">


                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <div class="section-title">
                                <h2><strong>Register</strong></h2>
                            </div>

                            <Formik
                initialValues={{ username: '', email: '', password:'', passwordRepeat:'' }}
                validate={values => {
                  const errors = {};
                  if(!values.username){
                    errors.username = '*Username Required';
                  }
                  else if (!values.email) {
                    errors.email = '*Email Required';
                  } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                  ) {
                    errors.email = 'Invalid email address';
                  } else if (!values.password) {
                    errors.password = '*Password Required';
                  }
                  else if (values.password.length < 8) {
                    errors.password = '*Password Should be 8 characters';
                  }
                  else if (values.password !== values.passwordRepeat) {
                    errors.passwordRepeat = '*Password not the same';
                  }

                  return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    setSubmitting(false);
                  }, 400);
                }}
              >
                {({ isSubmitting }) => (

                  <Form>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Username</span> </div>
                      <div class="row row-2"> <Field type="text" placeholder="Username" name="username" />
                        <ErrorMessage name="username" component="small" className='text-danger' /> </div>
                    </div>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                      <div class="row row-2"> <Field type="email" placeholder="Email" name="email" />
                        <ErrorMessage name="email" component="small" className='text-danger' />
                      </div>
                    </div>

                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                      <div class="row row-2"> <Field type="password" placeholder="Password" name="password" />
                        <ErrorMessage name="password" component="small" className='text-danger' />
                      </div>
                    </div>

                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                      <div class="row row-2"> <Field type="password" placeholder="Password Repeat" name="passwordRepeat" />
                        <ErrorMessage name="passwordRepeat" component="small" className='text-danger' />
                      </div>
                    </div>

                    <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>Register</b></button>
                  </Form>
                )}
              </Formik>
                        </div>
                    </div>

                </div>
            </section>

            <Latest_courses />
            <Back_to_top />
            <Footer />
        </>
    );
};

export default Register;
