import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './styles/App.css';
import './styles/Navbar.css';
import './styles/Homepage.css';
import './styles/Footer.css';
import './styles/Trending.css';
import './styles/login.css';
import './styles/available_course.css';
import './styles/cart.css';
import './styles/dashboard.css';
import AOS from 'aos';
import 'aos/dist/aos.css';
import Homepage from './components/landing_page/homepage';
import Login from './components/landing_page/login';
import Register from './components/landing_page/register';
import PasswordReset from './components/landing_page/password-reset';
import AccountVerify from './components/landing_page/account_verification';
import Available_courses from './components/landing_page/available_courses';
import Cart from './components/landing_page/cart';
import Payment from './components/landing_page/payment';
import BeTutor from './components/landing_page/be-a-tutor';
import Dashboard from './components/dashboard/dashboard';
import TutorHome from './components/tutors_portal/tutor_home';
import ScrollToTop from './components/scroll_to_top'



AOS.init({
  duration: 1000,
  easing: 'ease-in-out',
  once: true,
  mirror: false
});

function App() {


  return (
    <>

   <Router>
   <ScrollToTop>
   <Switch>
       <Route exact path="/" component={Homepage}/>
    </Switch>

    <Switch>
       <Route exact path="/login" component={Login}/>
    </Switch>

    <Switch>
       <Route exact path="/register" component={Register}/>
    </Switch>

    <Switch>
       <Route exact path="/password-reset" component={PasswordReset}/>
    </Switch>

    <Switch>
       <Route exact path="/account-verification/:email" component={AccountVerify}/>
    </Switch>

    <Switch>
       <Route exact path="/available-courses" component={Available_courses}/>
    </Switch>

    <Switch>
       <Route exact path="/cart" component={Cart}/>
    </Switch>

    <Switch>
       <Route exact path="/payment" component={Payment}/>
    </Switch>

    <Switch>
       <Route exact path="/be-a-tutor" component={BeTutor}/>
    </Switch>

    <Switch>
       <Route  exact path="/tutors-portal" component={TutorHome}/>
    </Switch>

    <Switch>
       <Route  path="/dashboard" component={Dashboard}/>
    </Switch>

   </ScrollToTop>



   </Router>

    </>
  );
}

export default App;
