import React, {useState} from 'react'
import BackTotop from './back_to_top'
import Footer from './footer'
import LatestCourses from './latest_courses'
import Navbar from './navbar'
import SearchResult from './search_result'
import TrendingCourses from './trending_courses'
import { ReactSearchAutocomplete } from 'react-search-autocomplete'
import axios from 'axios'
import { Baseurl } from '../baseurl'


const AvailableCourses = () => {
    const [search, setSearch] = useState('')
    const items = [
        {
          id: 0,
          name: 'Cobol'
        },
        {
          id: 1,
          name: 'Javascript'
        },
        {
          id: 2,
          name: 'Basic'
        },
        {
          id: 3,
          name: 'PHP'
        },
        {
          id: 4,
          name: 'Java'
        },
        {
            id: 5,
            name: 'Boostrap'
          },
          {
            id: 6,
            name: 'Marketing'
          }
      ]

   
    
      const handleOnSelect = (item) => {
        setSearch(item)
      }
    

      const formatResult = (item) => {
       return item;
       
      }


      const searchCourse = () =>{
         axios.get(`${Baseurl}searchcourse`,).then((res)=>{
             console.log(res.data)
         })
      }
    return (
        <>
          <Navbar/>
            <section className="about container-fluid mt-4" id="about">
                <div className='container-fluid'>
                    <div className='row'>
                        <div className='col-md-8 col-sm mx-auto'>
                            <img src="assets/img/logo.png" alt="" /> <br />
                            <small><strong><i class="fad fa-search" style={{ color: '#071B4D' }}></i> Search for a course</strong></small>
                            <small>
                            <ReactSearchAutocomplete
                             items={items}
                             onSelect={handleOnSelect}
                             autoFocus
                             formatResult={formatResult}
                             placeholder='Search by course name'
                              />
                            </small>
                            <button class="btn d-flex mx-auto" type="submit" onClick={searchCourse}><b>Search</b></button>
                        </div>

                    </div>
                </div>
                <div className="mt-5">
                    <SearchResult />
                </div>

                <div className="mt-5">
                    <LatestCourses />
                </div>

                <div className="mt-5">
                    <TrendingCourses />
                </div>
            </section>
            
            <BackTotop/>
            <Footer/>
        </>
    )
}

export default AvailableCourses
