import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';





const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [course, setCourse] = useState({})
    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setCourse(res.data)
            }
        })

    }, [])
    return (
        <div>
            <Navbar />
            <div className='container-fluid'>
                <div className='row'>
                    <div className='col-md-8'>
                        <section id="details" className="d-flex justify-content-center align-items-center">
                            <div className="container position-relative" data-aos="fade-in" data-aos-delay="100">
                                <h1>{course.course_title}</h1>
                                <p>{course.description}</p>
                                <div className='mt-3'>
                                    <small style={{ color: '#0D826E' }} className='px-2'><i class="fas fa-bookmark"></i><strong> Wishlist</strong></small>
                                    <small style={{ color: '#0D826E' }} className='px-2'><i class="fad fa-user-alt"></i><strong> 1200 Enrolled</strong></small>
                                    <small style={{ color: '#0D826E' }} className='px-2'><strong>
                                        <i class="fas fa-star" style={{ color: '#FFD700', fontSize: '13px' }}></i>
                                        <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i>
                                        <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i> </strong></small>
                                    <small style={{ color: '#0D826E' }} className='px-2 fst-italic'><strong>By Great</strong></small></div>
                            </div>

                            <div className="col-lg-6 order-1 order-lg-2 mb-4" data-aos="fade-in" data-aos-delay="100">
            <div class="login-card" style={{ borderRadius: '10px' }}>
              <img src="assets/img/logo.png" alt="" /> <br />
              <small><strong>Log in to Your Account</strong></small>
              <Formik
                initialValues={{ email: '', password: '' }}
                validate={values => {
                  const errors = {};
                  if (!values.email) {
                    errors.email = '*Email Required';
                  } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                  ) {
                    errors.email = 'Invalid email address';
                  } else if (!values.password) {
                    errors.password = '*Password Required';
                  }

                  return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                    // dispatch(isLogged(true));
                    // history.push('/dashboard')

                    axios.post(`${Baseurl}auth/login`, values).then((res) => {
                      if (res.data) {
                        setSubmitting(false);
                        let token = res.data.access_token
                        sessionStorage.setItem('Token', token)
                        dispatch(isLogged(true));
                        if (sessionStorage.Path) {
                          history.push(sessionStorage.Path)
                          sessionStorage.removeItem('Path')
                        } else {
                          history.push('/dashboard')
                        }
                      }
                    }).catch((error) => {
                      if (error.response.status === 401) {
                        setSubmitting(false);
                        toast("😔 Login details incorrect!", {
                          autoClose: 2000,
                        })
                      } else if (error.response.status === 500) {
                        setSubmitting(false);
                        sessionStorage.removeItem('Token')
                        toast("😔 Server is down!", {
                          autoClose: 2000,
                        })
                      }
                    })

                  }, 400);
                }}
              >
                {({ isSubmitting }) => (

                  <Form>
                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                      <div class="row row-2"> <Field type="email" placeholder="Registered Email" name="email" />
                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                    </div>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                      <div class="row row-2"> <Field type="password" placeholder="Password" name="password" />
                        <ErrorMessage name="password" component="small" className='text-danger' />
                      </div>
                    </div>
                    <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Login</b></button>
                  </Form>
                )}
              </Formik>
              <Link to='/password-reset'> <small style={{ cursor: 'pointer' }}><strong>Forgot Password?</strong></small>
              </Link>
            </div>
          </div>
                        </section>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}

export default CourseDetails
