import React, {useState, useEffect} from 'react'
import { Link as Link2, useHistory } from 'react-router-dom';
import { Link } from 'react-scroll';
import Navbar from './navbar';
import Footer from './footer'
import { Baseurl } from '../baseurl';
import axios from 'axios';
import Trending_courses from './trending_courses';
// import Swal from 'sweetalert2'
// import axios from 'axios';
// import {Baseurl} from '../components/Baseurl.js';

// const Toast = Swal.mixin({
//   toast: true,
//   position: 'top-end',
//   showConfirmButton: false,
//   timer: 500,
//   timerProgressBar: true,
//   didOpen: (toast) => {
//   toast.addEventListener('mouseenter', Swal.stopTimer)
//   toast.addEventListener('mouseleave', Swal.resumeTimer)
//   }
// })

// const Toast2 = Swal.mixin({
//   toast: true,
//   position: 'top-end',
//   showConfirmButton: false,
//   timer: 1500,
//   didOpen: (toast) => {
//   toast.addEventListener('mouseenter', Swal.stopTimer)
//   toast.addEventListener('mouseleave', Swal.resumeTimer)
//   }
// })


const Login = () => {
useEffect(() => {
  localStorage.removeItem('Email');

},[]);
let history = useHistory();
 const [isOpen, setIsOpen] = useState(false)
 const [value, setValue] = useState('Type your email');
 const [value2, setValue2] = useState('Type your password');
 const [isShow, setIsShow] = useState(false);
 const [isShow2, setIsShow2] = useState(false);
 const [login, setLogin] = useState({email: '', password: '' });
 const [isRoll, setIsRoll] = useState(false)

  const toggle = () => {
   setIsOpen(!isOpen);
  };
 
 const handleClick1 = () => {
  setValue('');
  setIsShow(true) 
 }
 const handleClick2 = () => {
  setValue2('');
  setIsShow2(true) 
 }

const handleChange = (e) => {
  let name = e.target.name
  let value = e.target.value
  setLogin({...login, [name]:value})
}
const submitForm = (e) => {
  setIsRoll(true)
  e.preventDefault();
  if (login.email && login.password){
    axios.post(`${Baseurl}login.php`, JSON.stringify(login)).then(res => {
      setIsRoll(false)
      let loginResponse = res.data;
      if (loginResponse.LoginSuccess) {
      // Toast.fire({
      // icon: 'success',
      // title: 'Logged in successfully'
      // })
      localStorage.setItem('Token', loginResponse.Auth);
      localStorage.removeItem('details');
      setTimeout(() => history.push("/dashboard"), 500);
      } else if(loginResponse.IncorrectPwd) {
        // Toast2.fire({
        //   icon: 'error',
        //   title: 'Incorrect password'
        // })
      } else if (loginResponse.EmailNotFound) {
        // Toast2.fire({
        //   icon: 'info',
        //   title: 'Email not found'
        // })
      }
    })
    
  } else {
    setIsRoll(false)
    // Toast2.fire({
    //   icon: 'info',
    //   title: 'Please fill all input'
    // })
  }
 
}
 
 return (
  <>
   <Navbar/>
   <section  className="about container-fluid mt-4" id="about">
     <div className="row">
      <div className="col-lg-5  d-lg-block order-2 order-lg-1" data-aos="fade-left" data-aos-delay="100">
      <img src="assets/img/student2.jpg" className="img-fluid login-img" alt="" style={{borderRadius:'10px'}}/>
     <small className='m-2'><strong><i class="fad fa-chalkboard-teacher" style={{color:'#0D826E'}}></i> Learn a skill today, Be smart!</strong></small>
 </div>

     <div className="col-lg-6 order-1 order-lg-2 mb-4" data-aos="fade-left" data-aos-delay="100">
   
     
     <div class="login-card" style={{borderRadius:'10px'}}>
     <div class="section-title">
      <h2><strong>LOGIN PAGE</strong></h2>
      </div>
   
    <form> 
      
        
        <div class="row-1">
            <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
            <div class="row row-2"> <input type="text" placeholder="Bojan Viner"/> </div>
        </div>

        
        <div class="row-1">
            <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
            <div class="row row-2"> <input type="text" placeholder="Bojan Viner"/> </div>
        </div>
       <button class="btn d-flex mx-auto"><b>Login</b></button>
    </form>
</div>
     </div>
    
    </div>
    </section>
  
    <Trending_courses/>
    
    <Footer/>
   </>
);
};

export default Login;
